import  { Component } from "react";

class Feedback extends Component{
    state = {
        good: 0,
        neutral: 0,
        bad: 0
    };
    incrementGood = () => {
        this.setState(prevState => {
            return {
                good: prevState.good + 1,
                total:prevState.good + 1,
            };

        });
    };
    incrementNeutral = () => {
        this.setState(prevState => {
            return {
                neutral: prevState.neutral+1,
            };

        });
    };
    incrementBad = () => {
        this.setState(prevState => {
            return {
                bad: prevState.bad+1,
            };

        });
    };

    total = () => {
        this.setState(prevState => {
            return {
                a: prevState.neutral,
            };

        });

    };    

    render() {
        return (
            <>
                <h1>Please leave feedback</h1>
                <button type='button' onClick={this.incrementGood}>good</button> 
                <button type='button' onClick={this.incrementNeutral}>neutral</button>
                <button type='button' onClick={this.incrementBad}>bad</button>
                <h2>Statistics</h2>
                <ul>
                    <li>good:{this.state.good}</li>
                    <li>neutral:{this.state.neutral}</li>
                    <li>bad:{this.state.bad}</li>
                    <li>total:`${this.state.good.value}`</li>
                </ul>
            </>
        );
}
}
export default Feedback;
