import { InputItem } from './inputForm.styled';
import { Formik, Form } from 'formik';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { useAddContactMutation } from '../../Redux/ContactsSlice';
import { nanoid } from 'nanoid';

export function InputForm() {
  const [addContact] = useAddContactMutation();

  const handleAddContact = async values => {
    try {
      await addContact(values);
      toast.success('Материал добавлен');
    } catch (error) {
      toast.error('Ошибка при добавлении материала');
      console.log(error);
    }
  };
  const contacts = useSelector(store => store.contacts.items);
  const dispatch = useDispatch();

  const onSubmit = (values, action) => {
    const contName = contacts.find(
      el => el.name.toLowerCase() === values.name.toLowerCase()
    );
    if (contName) return alert(contName.name + ' is already in contacts');
    values.id = nanoid();
    dispatch(addContact(values));
    action.resetForm();
  };
  return (
    <Formik initialValues={{ name: '', number: '' }} onSubmit={onSubmit}>
      <Form>
        <label>
          Name
          <InputItem
            type="text"
            name="name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
            required
          />
        </label>
        <label>
          Number
          <InputItem
            type="tel"
            name="number"
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
            required
          />
        </label>
        <button type="submit">Add contact</button>
      </Form>
    </Formik>
  );
}

InputForm.propTypes = {
  submitHandle: PropTypes.func,
};
